# Import librariesimport pandas as pdfrom pathlib import Pathimport os# Setting hyperparameterspath_root_dir = Path(os.getcwd()).parent.parentpath_data = f'{path_root_dir}/data/interim'# Read datadf = pd.read_csv(f'{path_data}/boston_housing_preprocessed.csv', index_col='ID')# Select features for modeling based on the previous explorationselected_feaures = [    'crim',     'zn',     'indus',     'chas',     'nox',     'rm',     'age',     'dis',     # 'rad',    'tax',    'ptratio',     'black',     'lstat',     'medv']df_model = df.loc[:, selected_feaures]# Saving outputdf_model.to_csv(f'{path_root_dir}/data/processed/boston_housing_model.csv')